import React, { useState } from 'react';
import './WalletPopup.css';

const WalletPopup = ({ isOpen, onClose }) => {
  const [activeTab, setActiveTab] = useState('deposit');

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  if (!isOpen) return null;

  return (
    <div className="wallet-popup">
      <div className="wallet-popup-content">
        <div className="wallet-popup-header">
          <h2>Wallet</h2>
          <button className="wallet-popup-close-btn" onClick={onClose}>Ã—</button>
        </div>
        <div className="wallet-popup-body">
          <div className="wallet-tabs">
            <div
              className={`wallet-tab ${activeTab === 'deposit' ? 'active' : ''}`}
              onClick={() => handleTabClick('deposit')}
            >
              Deposit
            </div>
            <div
              className={`wallet-tab ${activeTab === 'withdraw' ? 'active' : ''}`}
              onClick={() => handleTabClick('withdraw')}
            >
              Withdraw
            </div>
          </div>
          <div className="wallet-tab-content">
            {activeTab === 'deposit' && <div>Deposit Content</div>}
            {activeTab === 'withdraw' && <div>Withdraw Content</div>}
          </div>
        </div>
      </div>
    </div>
  );
};

export default WalletPopup;
