import React from 'react';
import './BetDetail.css';

const BetDetail = ({ selectedTeams, handleRemove }) => {
  return (
    <div className="bet-detail">
      <h2>Selected Bets</h2>
      {selectedTeams.length > 0 ? (
        <ul>
          {selectedTeams.map((team, index) => (
            <li key={index}>
              <div className="bet-detail-item">
                <div className="bet-detail-date">{team.date}</div>
                <div className="bet-detail-info">
                  <span className="bet-team-name">{team.name}</span>
                  <span className="bet-team-odds">{team.odds}</span>
                  <button className="remove-bet-btn" onClick={() => handleRemove(team.name)}>Remove</button>
                </div>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>No bets selected</p>
      )}
    </div>
  );
};

export default BetDetail;
