import React, { useState } from 'react';
import './WalletPopup.css';
import WalletVector from './icons/WalletVector.svg';
import DollarVector from './icons/DollarVector.svg';
import PlusIcon from './icons/PlusVector.svg'; // Import the plus icon

const WalletPopup = ({ isOpen, onClose, walletAmount }) => {
  const [activeTab, setActiveTab] = useState('deposit');
  const [selectedCard, setSelectedCard] = useState('');
  const [newCard, setNewCard] = useState({
    cardNumber: '',
    cardName: '',
    expiryMonth: '',
    expiryYear: '',
  });
  const [cvv, setCvv] = useState('');
  const [depositAmount, setDepositAmount] = useState('');
  const [isAddCardExpanded, setIsAddCardExpanded] = useState(false);
  const [errors, setErrors] = useState({});
  const [focused, setFocused] = useState({});

  const handleDeposit = () => {
    // Add deposit handling logic here
  };

  const toggleAddCard = () => {
    setIsAddCardExpanded(!isAddCardExpanded);
  };

  const handleNewCardChange = (event) => {
    const { name, value } = event.target;
    setNewCard((prevData) => ({
      ...prevData,
      [name]: value,
    }));
    setErrors((prevErrors) => ({
      ...prevErrors,
      [name]: undefined,
    }));
  };

  const handleBlur = (event) => {
    const { name } = event.target;
    setFocused((prevFocused) => ({
      ...prevFocused,
      [name]: false,
    }));

    if (newCard[name].trim() === '') {
      setErrors((prevErrors) => ({
        ...prevErrors,
        [name]: 'This field is required.',
      }));
    }
  };

  const handleFocus = (event) => {
    const { name } = event.target;
    setFocused((prevFocused) => ({
      ...prevFocused,
      [name]: true,
    }));
  };

  const handleSaveCard = () => {
    const newErrors = {};
    for (const field in newCard) {
      if (newCard[field].trim() === '') {
        newErrors[field] = 'This field is required.';
      }
    }
    if (Object.keys(newErrors).length === 0) {
      alert('Card saved successfully!');
      setIsAddCardExpanded(false);
    } else {
      setErrors(newErrors);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="wallet-popup">
      <div className="wallet-popup-content">
        <div className="wallet-popup-header">
          <h2>
            <img src={WalletVector} alt="Wallet" className="wallet-icon" /> Wallet
          </h2>
          <button className="wallet-close-btn" onClick={onClose}>×</button>
        </div>
        <div className="wallet-tabs">
          <div
            className={`wallet-tab ${activeTab === 'deposit' ? 'active' : ''}`}
            onClick={() => setActiveTab('deposit')}
          >
            Deposit
          </div>
          <div
            className={`wallet-tab ${activeTab === 'withdraw' ? 'active' : ''}`}
            onClick={() => setActiveTab('withdraw')}
          >
            Withdraw
          </div>
          <div className="wallet-tab-fill" style={{ left: activeTab === 'deposit' ? '0%' : '50%' }} />
        </div>
        <div className="wallet-balance-container">
          <div className="wallet-balance">
            ${walletAmount.toFixed(2)} <img src={DollarVector} alt="Dollar Icon" className="dollar-icon" />
          </div>
        </div>
        <div className="wallet-content">
          {activeTab === 'deposit' && (
            <div className="deposit-content">
              <label htmlFor="registered-cards">Registered Cards</label>
              <select
                id="registered-cards"
                value={selectedCard}
                onChange={(e) => setSelectedCard(e.target.value)}
              >
                <option value="" disabled selected>Select a card</option>
                {/* Add options for registered cards */}
              </select>
              <div className="add-new-card-container">
                <button
                  className={`add-new-card-btn ${isAddCardExpanded ? 'expanded' : ''}`}
                  onClick={toggleAddCard}
                >
                  <img src={PlusIcon} alt="Plus Icon" className="plus-icon" /> Add New Card
                  {isAddCardExpanded && (
                    <button className="add-new-card-close-btn" onClick={toggleAddCard}>×</button>
                  )}
                </button>
                {isAddCardExpanded && (
                  <div className="add-new-card-dropdown">
                    <input
                      type="text"
                      name="cardNumber"
                      placeholder="Card Number"
                      value={newCard.cardNumber}
                      onChange={handleNewCardChange}
                      onBlur={handleBlur}
                      onFocus={handleFocus}
                      className={errors.cardNumber ? (focused.cardNumber ? 'input-error-focused' : 'input-error') : ''}
                      required
                    />
                    {errors.cardNumber && (
                      <div className="error-message">
                        {errors.cardNumber}
                      </div>
                    )}
                    <input
                      type="text"
                      name="cardName"
                      placeholder="Name on Card"
                      value={newCard.cardName}
                      onChange={handleNewCardChange}
                      onBlur={handleBlur}
                      onFocus={handleFocus}
                      className={errors.cardName ? (focused.cardName ? 'input-error-focused' : 'input-error') : ''}
                      required
                    />
                    {errors.cardName && (
                      <div className="error-message">
                        {errors.cardName}
                      </div>
                    )}
                    <div className="expiry-date">
                      <label htmlFor="expiry-month">Expiry Date</label>
                      <div className="expiry-month-year">
                        <select
                          id="expiry-month"
                          name="expiryMonth"
                          value={newCard.expiryMonth}
                          onChange={handleNewCardChange}
                          onBlur={handleBlur}
                          onFocus={handleFocus}
                          className={errors.expiryMonth ? (focused.expiryMonth ? 'input-error-focused' : 'input-error') : ''}
                          required
                        >
                          <option value="" disabled selected>MM</option>
                          {/* Add options for months */}
                        </select>
                        {errors.expiryMonth && (
                          <div className="error-message">
                            {errors.expiryMonth}
                          </div>
                        )}
                        <select
                          id="expiry-year"
                          name="expiryYear"
                          value={newCard.expiryYear}
                          onChange={handleNewCardChange}
                          onBlur={handleBlur}
                          onFocus={handleFocus}
                          className={errors.expiryYear ? (focused.expiryYear ? 'input-error-focused' : 'input-error') : ''}
                          required
                        >
                          <option value="" disabled selected>YY</option>
                          {/* Add options for years */}
                        </select>
                        {errors.expiryYear && (
                          <div className="error-message">
                            {errors.expiryYear}
                          </div>
                        )}
                      </div>
                    </div>
                    <button className="save-card-btn" onClick={handleSaveCard}>Save</button>
                  </div>
                )}
              </div>
              {!isAddCardExpanded && (
                <>
                  <div className="expiry-date-cvv">
                    <div className="expiry-date">
                      <label htmlFor="expiry-month">Expiry Date</label>
                      <div className="expiry-month-year">
                        <select
                          id="expiry-month"
                          value={newCard.expiryMonth}
                          onChange={handleNewCardChange}
                        >
                          <option value="" disabled selected>MM</option>
                          {/* Add options for months */}
                        </select>
                        <select
                          id="expiry-year"
                          value={newCard.expiryYear}
                          onChange={handleNewCardChange}
                        >
                          <option value="" disabled selected>YY</option>
                          {/* Add options for years */}
                        </select>
                      </div>
                    </div>
                    <div className="cvv">
                      <label htmlFor="cvv">CVV</label>
                      <input
                        type="text"
                        id="cvv"
                        value={cvv}
                        onChange={(e) => setCvv(e.target.value)}
                      />
                    </div>
                  </div>
                  <label htmlFor="deposit-amount">Deposit Amount (min $10)</label>
                  <input
                    type="number"
                    id="deposit-amount"
                    value={depositAmount}
                    onChange={(e) => setDepositAmount(e.target.value)}
                    min="10"
                  />
                </>
              )}
              {!isAddCardExpanded && (
                <button className="deposit-btn" onClick={handleDeposit}>Deposit</button>
              )}
            </div>
          )}
          {activeTab === 'withdraw' && <p>Withdraw content goes here...</p>}
        </div>
      </div>
    </div>
  );
};

export default WalletPopup;
