import React from 'react';
import './RedeemPopup.css';
import WalletIcon from './icons/WalletVector.svg'; // Update the path to your icon
import CloseIcon from './icons/CloseVector.svg'; // Update the path to your close icon

const RedeemPopup = ({ isOpen, onClose }) => {
  if (!isOpen) return null;

  return (
    <div className="redeem-popup">
      <div className="redeem-popup-content">
        <div className="redeem-popup-header">
          <img src={WalletIcon} alt="Wallet Icon" className="popup-icon" />
          <span>Redeem Code</span>
          <button className="popup-close-btn" onClick={onClose}>
            <img src={CloseIcon} alt="Close" />
          </button>
        </div>
        <div className="redeem-popup-body">
          <h2>Redeem awesome deals!</h2>
          <p>Unlock incredible rewards, elevate gaming, and maximize winnings today!</p>
          <label htmlFor="promo-code">Enter Promotional Code</label>
          <input type="text" id="promo-code" placeholder="Enter a code" />
          <button className="redeem-btn">Redeem</button>
        </div>
      </div>
    </div>
  );
};

export default RedeemPopup;
