import React from 'react';
import './BetDetail.css';
import MyBetsVector from './icons/MyBetsVector.svg'; // Adjust the path to your icon file
import CloseVector from './icons/CloseVector.svg'; // Adjust the path to your close button SVG

const BetDetail = ({ bet, betCount, onClose }) => {
  return (
    <div className="bet-detail-container">
      <div className="bet-slip-header">
        <img src={MyBetsVector} alt="My Bets" className="bet-slip-icon" />
        <span className="bet-slip-title">Bet Slip</span>
        <img src={CloseVector} alt="Close" className="bet-slip-close" onClick={onClose} />
      </div>
      <div className="bet-slip-buttons">
          <button className="bet-slip-button">Single</button>
          <button className="bet-slip-button">Multi</button>
        </div>
      <div className="bet-detail-content">
        <h2>{bet.team}</h2>
        <p>Date: {bet.bet.date}</p>
        <p>Odds: {bet.odds}</p>
        {/* Add more details as necessary */}
      </div>
    </div>
  );
};

export default BetDetail;
