import React, { useState } from 'react';
import './ReferPopup.css';
import ReferVector from './icons/ReferVector.svg';
import PromoImage from './img/PromoImage.png'; // Ensure the correct path to the image

const ReferPopup = ({ isOpen, onClose, username }) => {
  const [activeTab, setActiveTab] = useState('referFriends');
  const [copied, setCopied] = useState(false);

  const referralLink = `https://elevate.com/?ref=${username.split('@')[0]}`;

  const handleCopy = () => {
    navigator.clipboard.writeText(referralLink);
    setCopied(true);
    setTimeout(() => setCopied(false), 3000);
  };

  if (!isOpen) return null;

  return (
    <div className={`refer-popup ${isOpen ? 'open' : ''}`}>
      <div className="refer-popup-content">
        <div className="refer-popup-header">
          <h2>
            <img src={ReferVector} alt="Refer" className="refer-popup-icon" /> Refer And Earn
          </h2>
          <button className="refer-popup-close-btn" onClick={onClose}>Ã—</button>
        </div>
        <div className="refer-popup-tabs">
          <div
            className={`refer-popup-tab ${activeTab === 'referFriends' ? 'active' : ''}`}
            onClick={() => setActiveTab('referFriends')}
          >
            Refer Friends
          </div>
          <div
            className={`refer-popup-tab ${activeTab === 'myEarnings' ? 'active' : ''}`}
            onClick={() => setActiveTab('myEarnings')}
          >
            My Earnings
          </div>
          <div className="refer-popup-tab-fill" style={{ left: activeTab === 'referFriends' ? '0%' : '50%' }} />
        </div>
        <div className="refer-popup-body">
          {activeTab === 'referFriends' && (
            <div className="refer-friends-section">
              <div className="refer-friends-text">
                <h3>Refer Your Friends</h3>
                <p>
                  Share your promo code "<strong>{username.split('@')[0]}</strong>" with your friends,
                  they'll be rewarded with a free boost to level 1 Roowards - and you'll receive
                  commission on all wagers, for life. Professional Publisher? Join Roobet Affiliates.
                </p>
                <label htmlFor="referral-link">Share Your Referral Link</label>
                <div className="refer-popup-link-container">
                  <input
                    type="text"
                    id="referral-link"
                    value={referralLink}
                    readOnly
                  />
                  <button className="refer-popup-copy-btn" onClick={handleCopy}>
                    {copied ? 'Copied!' : 'Copy Url'}
                  </button>
                </div>
              </div>
              <div className="refer-friends-image">
                <img src={PromoImage} alt="Promo" />
              </div>
            </div>
          )}
          {activeTab === 'myEarnings' && (
            <div>
              <h3>My Earnings</h3>
              <p>
                Your earnings from referred friends will be displayed here.
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ReferPopup;
