import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';
import RegistrationPopup from './RegistrationPopup';
import SignInPopup from './SignInPopup';
import ProfileIcon from './icons/ProfileVector.svg'; // Import the profile icon

function Header() {
  const [isRegistrationOpen, setIsRegistrationOpen] = useState(false);
  const [isSignInOpen, setIsSignInOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const openRegistrationPopup = () => {
    setIsRegistrationOpen(true);
  };

  const closeRegistrationPopup = () => {
    setIsRegistrationOpen(false);
  };

  const openSignInPopup = () => {
    setIsSignInOpen(true);
  };

  const closeSignInPopup = () => {
    setIsSignInOpen(false);
  };

  // Mock login function
  const handleLogin = () => {
    setIsLoggedIn(true);
    setIsSignInOpen(false);
  };

  return (
    <header className={`header d-flex justify-content-between align-items-center px-4 ${isLoggedIn ? 'logged-in' : ''}`}>
      <div className="logo-wrapper">
        <div className="logo">
          <Link to="/">Elevate</Link>
        </div>
      </div>
      {isLoggedIn ? (
        <>
          <button className="wallet-button btn">Wallet</button>
          <div className="profile-icon">
            <img src={ProfileIcon} alt="Profile" />
          </div>
        </>
      ) : (
        <div className="auth-buttons">
          <button className="login-button btn me-3" onClick={openSignInPopup}>Login</button>
          <button className="register-button btn" onClick={openRegistrationPopup}>Register</button>
        </div>
      )}
      {isRegistrationOpen && <RegistrationPopup isOpen={isRegistrationOpen} onClose={closeRegistrationPopup} />}
      {isSignInOpen && <SignInPopup isOpen={isSignInOpen} onClose={closeSignInPopup} openRegistrationPopup={openRegistrationPopup} handleLogin={handleLogin} />}
    </header>
  );
}

export default Header;
