import React, { useState } from 'react';
import './BetDetail.css';
import MyBetsVector from './icons/MyBetsVector.svg'; // Adjust the path to your icon file
import CloseVector from './icons/CloseVector.svg'; // Adjust the path to your close button SVG
import DropdownVector from './icons/DropdownVector.svg'; // Import the dropdown icon
import DollarVector from './icons/DollarVector.svg'; // Import the dollar icon

const BetDetail = ({ bets, onClose }) => {
  const [activeTab, setActiveTab] = useState('Single');
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState('Accept Any Odds');

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleOptionClick = (option) => {
    setSelectedOption(option);
    setDropdownOpen(false);
  };

  return (
    <div className="bet-detail-container">
      <div className="bet-header-fixed">
        <div className="bet-slip-header">
          <div className="bet-slip-header-content">
            <img src={MyBetsVector} alt="My Bets" className="bet-slip-icon" />
            <span className="bet-slip-title">Bet Slip</span>
            <img src={CloseVector} alt="Close" className="bet-slip-close" onClick={onClose} />
          </div>
        </div>
        <div className="bet-slip-tabs">
          <button
            className={`bet-slip-tab ${activeTab === 'Single' ? 'active' : ''}`}
            onClick={() => handleTabClick('Single')}
          >
            Single
          </button>
          <button
            className={`bet-slip-tab ${activeTab === 'Multi' ? 'active' : ''}`}
            onClick={() => handleTabClick('Multi')}
          >
            Multi
          </button>
        </div>
        <hr className='hr-line-bet-slip' />
        <div className="bet-options">
          <div className="dropdown">
            <button className="dropbtn" onClick={toggleDropdown}>
              {selectedOption}
              <img src={DropdownVector} alt="Dropdown Icon" className="dropdown-icon" />
            </button>
            {dropdownOpen && (
              <div className="dropdown-content show">
                <a
                  href="#"
                  onClick={() => handleOptionClick('Accept Any Odds')}
                  className={selectedOption === 'Accept Any Odds' ? 'selected' : ''}
                >
                  Accept Any Odds
                </a>
                <a
                  href="#"
                  onClick={() => handleOptionClick('Accept Only Higher Odds')}
                  className={selectedOption === 'Accept Only Higher Odds' ? 'selected' : ''}
                >
                  Accept Only Higher Odds
                </a>
                <a
                  href="#"
                  onClick={() => handleOptionClick('No Odds Changes Accepted')}
                  className={selectedOption === 'No Odds Changes Accepted' ? 'selected' : ''}
                >
                  No Odds Changes Accepted
                </a>
              </div>
            )}
          </div>
          <span className="clear-all">Clear All</span>
        </div>
      </div>
      <div className="bet-detail-content-new">
        {bets.map((bet, index) => (
          <div key={index} className="bet-item-bet">
            <div className="bet-item-header">
              <span>{bet.team} vs {bet.opponent}</span>
              <img src={CloseVector} alt="Remove" className="bet-item-close" onClick={() => onClose(bet)} />
            </div>
            <div className="bet-item-content">
              <div className="bet-item-details">
                <span className="bet-item-team">{bet.team}</span>
                <span className="bet-item-odds">{bet.odds}</span>
              </div>
              <div className="bet-item-wager">
                <input type="number" defaultValue={0.00} onChange={(e) => bet.wager = e.target.value} />
                <img src={DollarVector} alt="Dollar Icon" className="currency-icon" />
              </div>
              <div className="bet-item-payout">
                <span>Est. Payout</span>
                <span>{(bet.wager * bet.odds).toFixed(2)} <img src={DollarVector} alt="Dollar Icon" className="currency-icon" /></span>
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className="bet-footer">
        <div className="bet-summary">
          <div className="bet-summary-item">
            <span className="bet-summary-label">Total Wager</span>
            <span className="bet-summary-label">Est. Payout</span>

          </div>
          <div className="bet-summary-item">
          <span className="bet-summary-value">
              {bets.reduce((total, bet) => total + parseFloat(bet.wager || 0), 0).toFixed(2)}
              <img src={DollarVector} alt="Dollar Icon" className="currency-icon" />
            </span>
            <span className="bet-summary-value">
              {bets.reduce((total, bet) => total + (parseFloat(bet.wager || 0) * bet.odds), 0).toFixed(2)}
              <img src={DollarVector} alt="Dollar Icon" className="currency-icon" />
            </span>
          </div>
        </div>
        <button className="bet-button">Bet</button>
      </div>
    </div>
  );
};

export default BetDetail;
