import React, { useState } from 'react';
import './WalletPopup.css';
import WalletVector from './icons/WalletVector.svg'; // Adjust the path as necessary

const WalletPopup = ({ isOpen, onClose }) => {
  const [activeTab, setActiveTab] = useState('deposit');

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  if (!isOpen) return null;

  return (
    <div className="wallet-popup">
      <div className="wallet-popup-content">
        <div className="wallet-popup-header">
          <img src={WalletVector} alt="Wallet Icon" className="wallet-icon" />
          <h2>Wallet</h2>
          <button className="wallet-close-btn" onClick={onClose}>Ã—</button>
        </div>
        <div className="wallet-tabs">
          <div className={`wallet-tab ${activeTab === 'deposit' ? 'active' : ''}`} onClick={() => handleTabChange('deposit')}>
            Deposit
          </div>
          <div className={`wallet-tab ${activeTab === 'withdraw' ? 'active' : ''}`} onClick={() => handleTabChange('withdraw')}>
            Withdraw
          </div>
          <div className="wallet-tab-fill" style={{ left: activeTab === 'deposit' ? '0%' : '50%' }}></div>
        </div>
        <div className="wallet-content">
          {activeTab === 'deposit' && <p>Deposit content goes here...</p>}
          {activeTab === 'withdraw' && <p>Withdraw content goes here...</p>}
        </div>
      </div>
    </div>
  );
};

export default WalletPopup;
