import React, { useState } from 'react';
import './ReferPopup.css';

const ReferPopup = ({ isOpen, onClose, username }) => {
  const cleanUsername = username.includes('@') ? username.split('@')[0] : username;
  const referralLink = `https://elevate.com/?ref=${cleanUsername}`;
  const [copied, setCopied] = useState(false);

  const handleCopy = () => {
    navigator.clipboard.writeText(referralLink);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  if (!isOpen) return null;

  return (
    <div className={`refer-popup ${isOpen ? 'open' : ''}`}>
      <div className="refer-popup-content">
        <div className="refer-popup-header">
          <h2>Refer And Earn</h2>
          <button className="refer-popup-close-btn" onClick={onClose}>Ã—</button>
        </div>
        <div className="refer-popup-tabs">
          <div className="refer-popup-tab active">Refer Friends</div>
          <div className="refer-popup-tab">My Earnings</div>
        </div>
        <div className="refer-popup-body">
          <div className="refer-popup-left">
            <h3>Refer Your Friends</h3>
            <p>
              Share your promo code "<strong>{cleanUsername}</strong>" with your friends, they'll be rewarded with a
              free boost to level 1 Roowards - and you'll receive commission on all wagers, for life.<br />
              Professional Publisher?<br />
              <a href="https://roobet.com/affiliates" target="_blank" rel="noopener noreferrer">Join Roobet Affiliates</a>
            </p>
            <label htmlFor="referral-link">Share Your Referral Link</label>
            <div className="refer-popup-code-container">
              <input
                type="text"
                id="referral-link"
                value={referralLink}
                readOnly
              />
              <button className="refer-popup-copy-btn" onClick={handleCopy}>
                {copied ? 'Copied!' : 'Copy Url'}
              </button>
            </div>
          </div>
          <div className="refer-popup-right">
            <img src="./img/RedeemPicture.png" alt="Refer Friends" className="refer-popup-picture" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default ReferPopup;
