import React, { useState } from 'react';
import './BasketballBets.css';

const BasketballBets = ({ onBetClick, selectedBet }) => {
  const betItems = [
    {
      id: 1,
      date: "Mon. Jun 24, 22:00",
      teams: [
        { name: "Toronto Raptors", odds: "1.78" },
        { name: "Houston Rockets", odds: "2.20" }
      ],
      title: "Raptors vs Rockets",
      description: "Bet on the Raptors vs Rockets game happening on Mon. Jun 24, 22:00."
    },
    {
      id: 2,
      date: "Tues. Jun 25, 20:30",
      teams: [
        { name: "Boston Celtics", odds: "1.83" },
        { name: "Dallas Mavericks", odds: "2.03" }
      ],
      title: "Celtics vs Mavericks",
      description: "Bet on the Celtics vs Mavericks game happening on Tues. Jun 25, 20:30."
    },
    {
      id: 3,
      date: "Mon. Jun 25, 21:00",
      teams: [
        { name: "Los Angeles Lakers", odds: "3.23" },
        { name: "Denver Nuggets", odds: "1.26" }
      ],
      title: "Lakers vs Nuggets",
      description: "Bet on the Lakers vs Nuggets game happening on Mon. Jun 25, 21:00."
    },
    {
      id: 4,
      date: "Tues. Jun 26, 20:00",
      teams: [
        { name: "Phoenix Suns", odds: "1.06" },
        { name: "Washington Wizards", odds: "3.12" }
      ],
      title: "Suns vs Wizards",
      description: "Bet on the Suns vs Wizards game happening on Tues. Jun 26, 20:00."
    }
  ];

  return (
    <div className="main-content basketball-bets-page">
      <h1>Basketball</h1>
      <div className="bets-list">
        {betItems.map((bet, index) => (
          <div
            className={`bet-item ${selectedBet && selectedBet.id === bet.id ? 'selected' : ''}`}
            key={index}
            onClick={() => onBetClick(bet)}
          >
            <div className="bet-date">{bet.date}</div>
            <div className="bet-teams">
              <div className="team-info">
                {bet.teams.map((team, idx) => (
                  <div className="team" key={idx}>{team.name}</div>
                ))}
              </div>
              <div className="bet-odds-wrapper">
                <div className="bet-odds">
                  <div className="winner">Winner (incl. overtime)</div>
                  <div className="odds">
                    {bet.teams.map((team, idx) => (
                      <div
                        className={`team-odds ${selectedBet && selectedBet.id === bet.id ? 'selected' : ''}`}
                        key={idx}
                      >
                        <div className="odds-box">
                          <span className="team-name">{team.name}</span>
                          <span className="odds-value">{team.odds}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BasketballBets;
