import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';
import RegistrationPopup from './RegistrationPopup';
import SignInPopup from './SignInPopup';
import WalletPopup from './WalletPopup'; // Import WalletPopup
import DollarVector from './icons/DollarVector.svg';
import ProfileVector from './icons/ProfileVector.svg';
import WalletVector from './icons/WalletVector.svg';

function Header({ isLoggedIn, onLoginSuccess, openRegistrationPopup, openSignInPopup }) {
  const [isWalletOpen, setIsWalletOpen] = useState(false); // State for wallet popup

  const openWalletPopup = () => {
    setIsWalletOpen(true);
  };

  const closeWalletPopup = () => {
    setIsWalletOpen(false);
  };

  return (
    <header className={`header ${isLoggedIn ? 'logged-in' : ''}`}>
      <div className="logo-wrapper">
        <div className="logo"><Link to="/">Elevate</Link></div>
      </div>
      {!isLoggedIn && (
        <div className="auth-buttons">
          <button className="login-button btn me-3" onClick={openSignInPopup}>Login</button>
          <button className="register-button btn" onClick={openRegistrationPopup}>Register</button>
        </div>
      )}
      {isLoggedIn && (
        <>
          <div className="wallet-container">
            <div className="wallet-amount">
              0.00 <img src={DollarVector} alt="Dollar Icon" />
            </div>
            <button className="wallet-button" onClick={openWalletPopup}>
              <img src={WalletVector} alt="Wallet Icon" className="wallet-icon" />
              Wallet
            </button>
          </div>
          <div className="profile-icon">
            <img src={ProfileVector} alt="Profile" />
          </div>
        </>
      )}
      <WalletPopup isOpen={isWalletOpen} onClose={closeWalletPopup} />
    </header>
  );
}

export default Header;
